import { useState, useEffect } from "react";
import {
  Card,
  Title,
  Text,
  Button,
  Table,
  Checkbox,
  TextInput,
  Group,
  Stack,
  Badge,
  ActionIcon,
  Pagination,
  Select,
  Switch,
  Menu,
  Modal,
  Flex,
  ScrollArea,
} from "@mantine/core";
import {
  IconSearch,
  IconDownload,
  IconTrash,
  IconFilter,
  IconDots,
  IconEdit,
  IconEye,
  IconBrain,
} from "@tabler/icons-react";
import { useApi } from "../hooks/useApi";
import {
  fitmentsService,
  fitmentUploadService,
  type FlattenedAppliedFitment,
} from "../api/services";

export default function Fitments() {
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedFitments, setSelectedFitments] = useState<string[]>([]);
  const [expandedView, setExpandedView] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const [sortBy, setSortBy] = useState("partId");
  const [sortOrder, setSortOrder] = useState<"asc" | "desc">("asc");
  const [deleteModalOpen, setDeleteModalOpen] = useState(false);
  const { data, loading, error, refetch } = useApi<{
    fitments: FlattenedAppliedFitment[];
    totalCount: number;
  }>(
    () =>
      fitmentsService.getFitments({
        search: searchTerm || undefined,
        sortBy,
        sortOrder,
        page: currentPage,
        pageSize: 50,
      }),
    [searchTerm, sortBy, sortOrder, currentPage]
  );

  // Fetch AI-generated fitments from Django backend
  const {
    data: aiFitmentsData,
    loading: aiLoading,
    refetch: refetchAi,
  } = useApi<{ fitments: any[]; total_fitments: number }>(
    () => fitmentUploadService.getAppliedFitments(),
    []
  );

  const fitments = data?.fitments ?? [];
  const aiFitments = aiFitmentsData?.fitments ?? [];

  // Debug logging
  console.log("AI Fitments Data:", aiFitmentsData);
  console.log("AI Fitments Array:", aiFitments);
  useEffect(() => {
    const onFocus = () => {
      refetch();
      refetchAi();
    };
    const onVisibility = () => {
      if (document.visibilityState === "visible") {
        refetch();
        refetchAi();
      }
    };
    window.addEventListener("focus", onFocus);
    document.addEventListener("visibilitychange", onVisibility);
    return () => {
      window.removeEventListener("focus", onFocus);
      document.removeEventListener("visibilitychange", onVisibility);
    };
  }, [refetch, refetchAi]);
  const handleExport = () => {
    const params = new URLSearchParams();
    if (searchTerm) params.set("search", searchTerm);
    if (sortBy) params.set("sortBy", sortBy);
    if (sortOrder) params.set("sortOrder", sortOrder);
    window.location.href = `http://localhost:8000/api/fitments/export?${params.toString()}`;
  };

  const handleSelectAll = (checked: boolean) => {
    if (checked) {
      setSelectedFitments(fitments.map((f) => f.hash));
    } else {
      setSelectedFitments([]);
    }
  };

  const handleSelectFitment = (hash: string, checked: boolean) => {
    if (checked) {
      setSelectedFitments((prev) => [...prev, hash]);
    } else {
      setSelectedFitments((prev) => prev.filter((h) => h !== hash));
    }
  };

  const handleBulkDelete = () => {
    console.log("Deleting fitments:", selectedFitments);
    setSelectedFitments([]);
    setDeleteModalOpen(false);
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case "Active":
        return "green";
      case "Inactive":
        return "red";
      case "Sunset":
        return "orange";
      default:
        return "gray";
    }
  };

  return (
    <div style={{ padding: "24px 0" }}>
      <Card shadow="sm" padding="lg" radius="md" withBorder>
        <Stack gap="lg">
          {/* Header */}
          <Group justify="space-between">
            <div>
              <Title order={2}>Fitments Management</Title>
              <Text c="dimmed">
                View and manage all fitments with advanced filtering
              </Text>
            </div>
            <Group>
              <Switch
                label="Expanded View"
                checked={expandedView}
                onChange={(event) =>
                  setExpandedView(event.currentTarget.checked)
                }
              />
            </Group>
          </Group>

          {/* Filters and Actions */}
          <Group justify="space-between">
            <Group>
              <TextInput
                placeholder="Search by Part ID, Make, Model..."
                leftSection={<IconSearch size={16} />}
                value={searchTerm}
                onChange={(event) => setSearchTerm(event.currentTarget.value)}
                style={{ minWidth: 300 }}
              />
              <Select
                placeholder="Sort by"
                value={sortBy}
                onChange={(value) => setSortBy(value || "partId")}
                data={[
                  { value: "partId", label: "Part ID" },
                  { value: "makeName", label: "Make" },
                  { value: "modelName", label: "Model" },
                  { value: "year", label: "Year" },
                  { value: "updatedAt", label: "Last Updated" },
                ]}
                leftSection={<IconFilter size={16} />}
              />
              <Select
                value={sortOrder}
                onChange={(value) => setSortOrder(value as "asc" | "desc")}
                data={[
                  { value: "asc", label: "Ascending" },
                  { value: "desc", label: "Descending" },
                ]}
                w={120}
              />
            </Group>

            <Group>
              {selectedFitments.length > 0 && (
                <Button
                  leftSection={<IconTrash size={16} />}
                  color="red"
                  variant="light"
                  onClick={() => setDeleteModalOpen(true)}
                >
                  Delete ({selectedFitments.length})
                </Button>
              )}
              <Button
                leftSection={<IconDownload size={16} />}
                variant="filled"
                onClick={handleExport}
              >
                Export CSV
              </Button>
            </Group>
          </Group>

          {/* Selection Summary */}
          {selectedFitments.length > 0 && (
            <Group
              justify="space-between"
              p="sm"
              style={{
                backgroundColor: "var(--mantine-color-blue-0)",
                borderRadius: 4,
              }}
            >
              <Text size="sm" fw={500}>
                {selectedFitments.length} of {fitments.length} fitments selected
              </Text>
              <Button
                size="xs"
                variant="light"
                onClick={() => setSelectedFitments([])}
              >
                Clear Selection
              </Button>
            </Group>
          )}

          {/* AI Generated Fitments Section */}
          {aiFitments.length > 0 ? (
            <Card
              shadow="lg"
              padding="lg"
              radius="xl"
              withBorder
              style={{
                background: "linear-gradient(145deg, #f8fafc 0%, #f1f5f9 100%)",
                border: "2px solid #8b5cf6",
              }}
            >
              <Stack gap="lg">
                <Group justify="space-between">
                  <div>
                    <Group gap="sm" mb="xs">
                      <IconBrain size={24} color="#8b5cf6" />
                      <Title order={3} c="violet">
                        AI Generated Fitments
                      </Title>
                    </Group>
                    <Text size="sm" c="dimmed">
                      {aiFitments.length} fitments generated and applied by AI
                    </Text>
                  </div>
                  <Group>
                    <Button
                      variant="light"
                      color="violet"
                      onClick={() => refetchAi()}
                      loading={aiLoading}
                    >
                      Refresh
                    </Button>
                    <Button
                      leftSection={<IconDownload size={16} />}
                      variant="light"
                      color="violet"
                      onClick={() => {
                        const blob = new Blob(
                          [JSON.stringify(aiFitments, null, 2)],
                          {
                            type: "application/json",
                          }
                        );
                        const url = window.URL.createObjectURL(blob);
                        const link = document.createElement("a");
                        link.href = url;
                        link.download = "ai-fitments.json";
                        document.body.appendChild(link);
                        link.click();
                        document.body.removeChild(link);
                        window.URL.revokeObjectURL(url);
                      }}
                    >
                      Export JSON
                    </Button>
                    <Button
                      leftSection={<IconDownload size={16} />}
                      variant="gradient"
                      gradient={{ from: "violet.6", to: "purple.6", deg: 135 }}
                      onClick={() => {
                        // Export as CSV
                        const headers = [
                          "Part ID",
                          "Part Description",
                          "Year",
                          "Make",
                          "Model",
                          "Submodel",
                          "Drive Type",
                          "Position",
                          "Quantity",
                          "Applied Date",
                        ];
                        const csvContent = [
                          headers.join(","),
                          ...aiFitments.map((fitment: any) =>
                            [
                              fitment.partId,
                              `"${fitment.partDescription}"`,
                              fitment.year,
                              fitment.make,
                              fitment.model,
                              fitment.submodel,
                              fitment.driveType,
                              fitment.position || "Universal",
                              fitment.quantity,
                              new Date(fitment.appliedAt).toLocaleDateString(),
                            ].join(",")
                          ),
                        ].join("\n");

                        const blob = new Blob([csvContent], {
                          type: "text/csv",
                        });
                        const url = window.URL.createObjectURL(blob);
                        const link = document.createElement("a");
                        link.href = url;
                        link.download = "ai-fitments.csv";
                        document.body.appendChild(link);
                        link.click();
                        document.body.removeChild(link);
                        window.URL.revokeObjectURL(url);
                      }}
                    >
                      Export CSV
                    </Button>
                  </Group>
                </Group>

                {/* AI Fitments Summary */}
                <Group
                  justify="space-between"
                  p="md"
                  style={{
                    background:
                      "linear-gradient(135deg, #f3e8ff 0%, #e9d5ff 100%)",
                    borderRadius: "12px",
                    border: "1px solid #c4b5fd",
                  }}
                >
                  <Group>
                    <Badge variant="light" color="violet" size="lg">
                      Total AI Fitments: {aiFitments.length}
                    </Badge>
                    <Badge variant="light" color="green" size="lg">
                      Unique Parts:{" "}
                      {new Set(aiFitments.map((f: any) => f.partId)).size}
                    </Badge>
                    <Badge variant="light" color="blue" size="lg">
                      Unique Vehicles:{" "}
                      {
                        new Set(
                          aiFitments.map(
                            (f: any) => `${f.year}-${f.make}-${f.model}`
                          )
                        ).size
                      }
                    </Badge>
                  </Group>
                  <Text size="sm" fw={500} c="violet">
                    Last Applied:{" "}
                    {new Date(
                      Math.max(
                        ...aiFitments.map((f: any) =>
                          new Date(f.appliedAt).getTime()
                        )
                      )
                    ).toLocaleDateString()}
                  </Text>
                </Group>

                <ScrollArea h={400}>
                  <Table striped highlightOnHover>
                    <Table.Thead>
                      <Table.Tr>
                        <Table.Th>Part ID</Table.Th>
                        <Table.Th>Part Description</Table.Th>
                        <Table.Th>Vehicle Details</Table.Th>
                        <Table.Th>Position</Table.Th>
                        <Table.Th>Quantity</Table.Th>
                        <Table.Th>Applied Date</Table.Th>
                        <Table.Th>Actions</Table.Th>
                      </Table.Tr>
                    </Table.Thead>
                    <Table.Tbody>
                      {aiFitments.map((fitment: any, index: number) => (
                        <Table.Tr key={index}>
                          <Table.Td>
                            <Text fw={500} size="sm" c="violet">
                              {fitment.partId}
                            </Text>
                          </Table.Td>
                          <Table.Td>
                            <Text size="sm" lineClamp={2} maw={200}>
                              {fitment.partDescription}
                            </Text>
                          </Table.Td>
                          <Table.Td>
                            <div>
                              <Text size="sm" fw={500}>
                                {fitment.year} {fitment.make} {fitment.model}
                              </Text>
                              <Text size="xs" c="dimmed">
                                {fitment.submodel} â€¢ {fitment.driveType}
                              </Text>
                            </div>
                          </Table.Td>
                          <Table.Td>
                            <Badge variant="light" size="sm" color="cyan">
                              {fitment.position || "Universal"}
                            </Badge>
                          </Table.Td>
                          <Table.Td>
                            <Text size="sm" fw={500} ta="center">
                              {fitment.quantity}
                            </Text>
                          </Table.Td>
                          <Table.Td>
                            <Text size="xs" c="dimmed">
                              {new Date(fitment.appliedAt).toLocaleDateString()}
                            </Text>
                          </Table.Td>
                          <Table.Td>
                            <Menu shadow="md" width={200}>
                              <Menu.Target>
                                <ActionIcon
                                  variant="light"
                                  size="sm"
                                  color="violet"
                                >
                                  <IconDots size={16} />
                                </ActionIcon>
                              </Menu.Target>
                              <Menu.Dropdown>
                                <Menu.Item leftSection={<IconEye size={14} />}>
                                  View Details
                                </Menu.Item>
                                <Menu.Item leftSection={<IconEdit size={14} />}>
                                  Edit Fitment
                                </Menu.Item>
                                <Menu.Divider />
                                <Menu.Item
                                  leftSection={<IconTrash size={14} />}
                                  color="red"
                                >
                                  Delete
                                </Menu.Item>
                              </Menu.Dropdown>
                            </Menu>
                          </Table.Td>
                        </Table.Tr>
                      ))}
                    </Table.Tbody>
                  </Table>
                </ScrollArea>
              </Stack>
            </Card>
          ) : (
            <Card
              shadow="sm"
              padding="xl"
              radius="xl"
              withBorder
              style={{
                background: "linear-gradient(145deg, #f8fafc 0%, #f1f5f9 100%)",
                border: "2px dashed #8b5cf6",
                textAlign: "center",
              }}
            >
              <Stack gap="md" align="center">
                <IconBrain size={48} color="#8b5cf6" />
                <div>
                  <Title order={3} c="violet" mb="xs">
                    No AI Generated Fitments Yet
                  </Title>
                  <Text size="sm" c="dimmed" mb="lg">
                    Upload your VCDB and Products files in the Apply Fitments
                    page to generate AI-powered fitment suggestions.
                  </Text>
                </div>
                <Button
                  variant="gradient"
                  gradient={{ from: "violet.6", to: "purple.6", deg: 135 }}
                  leftSection={<IconBrain size={16} />}
                  onClick={() => {
                    // Navigate to Apply Fitments page
                    window.location.href = "/apply-fitments";
                  }}
                >
                  Go to Apply Fitments
                </Button>
              </Stack>
            </Card>
          )}

          {/* Table */}
          {error && <Text c="red">{error}</Text>}
          {(loading || aiLoading) && <Text>Loading...</Text>}
          <ScrollArea>
            <Table striped highlightOnHover>
              <Table.Thead>
                <Table.Tr>
                  <Table.Th>
                    <Checkbox
                      checked={selectedFitments.length === fitments.length}
                      indeterminate={
                        selectedFitments.length > 0 &&
                        selectedFitments.length < fitments.length
                      }
                      onChange={(event) =>
                        handleSelectAll(event.currentTarget.checked)
                      }
                    />
                  </Table.Th>
                  <Table.Th>Part ID</Table.Th>
                  <Table.Th>Status</Table.Th>
                  <Table.Th>Vehicle</Table.Th>
                  <Table.Th>Part Type</Table.Th>
                  <Table.Th>Position</Table.Th>
                  <Table.Th>Title</Table.Th>
                  {expandedView && (
                    <>
                      <Table.Th>Description</Table.Th>
                      <Table.Th>Quantity</Table.Th>
                      <Table.Th>Lift Height</Table.Th>
                      <Table.Th>Wheel Type</Table.Th>
                      <Table.Th>Updated</Table.Th>
                    </>
                  )}
                  <Table.Th>Actions</Table.Th>
                </Table.Tr>
              </Table.Thead>
              <Table.Tbody>
                {fitments.map((fitment) => (
                  <Table.Tr key={fitment.hash}>
                    <Table.Td>
                      <Checkbox
                        checked={selectedFitments.includes(fitment.hash)}
                        onChange={(event) =>
                          handleSelectFitment(
                            fitment.hash,
                            event.currentTarget.checked
                          )
                        }
                      />
                    </Table.Td>
                    <Table.Td>
                      <Text fw={500}>{fitment.partId}</Text>
                    </Table.Td>
                    <Table.Td>
                      <Badge
                        variant="light"
                        color={getStatusColor(fitment.itemStatus)}
                        size="sm"
                      >
                        {fitment.itemStatus}
                      </Badge>
                    </Table.Td>
                    <Table.Td>
                      <div>
                        <Text size="sm" fw={500}>
                          {fitment.year} {fitment.makeName} {fitment.modelName}
                        </Text>
                        <Text size="xs" c="dimmed">
                          {fitment.subModelName} â€¢ {fitment.driveTypeName}
                        </Text>
                      </div>
                    </Table.Td>
                    <Table.Td>{fitment.partTypeDescriptor}</Table.Td>
                    <Table.Td>{fitment.position}</Table.Td>
                    <Table.Td>{fitment.fitmentTitle}</Table.Td>
                    {expandedView && (
                      <>
                        <Table.Td>
                          <Text size="sm" truncate="end" maw={200}>
                            {fitment.fitmentDescription}
                          </Text>
                        </Table.Td>
                        <Table.Td>{fitment.quantity}</Table.Td>
                        <Table.Td>{fitment.liftHeight}</Table.Td>
                        <Table.Td>{fitment.wheelType}</Table.Td>
                        <Table.Td>
                          <Text size="xs" c="dimmed">
                            {new Date(fitment.updatedAt).toLocaleDateString()}
                          </Text>
                        </Table.Td>
                      </>
                    )}
                    <Table.Td>
                      <Menu shadow="md" width={200}>
                        <Menu.Target>
                          <ActionIcon variant="light" size="sm">
                            <IconDots size={16} />
                          </ActionIcon>
                        </Menu.Target>
                        <Menu.Dropdown>
                          <Menu.Item leftSection={<IconEye size={14} />}>
                            View Details
                          </Menu.Item>
                          <Menu.Item leftSection={<IconEdit size={14} />}>
                            Edit Fitment
                          </Menu.Item>
                          <Menu.Divider />
                          <Menu.Item
                            leftSection={<IconTrash size={14} />}
                            color="red"
                          >
                            Delete
                          </Menu.Item>
                        </Menu.Dropdown>
                      </Menu>
                    </Table.Td>
                  </Table.Tr>
                ))}
              </Table.Tbody>
            </Table>
          </ScrollArea>

          {/* Pagination */}
          <Flex justify="space-between" align="center">
            <Text size="sm" c="dimmed">
              Showing 1-{fitments.length} of{" "}
              {data?.totalCount ?? fitments.length} fitments
            </Text>
            <Pagination
              value={currentPage}
              onChange={setCurrentPage}
              total={5}
            />
          </Flex>
        </Stack>
      </Card>

      {/* Delete Confirmation Modal */}
      <Modal
        opened={deleteModalOpen}
        onClose={() => setDeleteModalOpen(false)}
        title="Confirm Deletion"
        centered
      >
        <Stack gap="md">
          <Text>
            Are you sure you want to delete {selectedFitments.length} fitment
            {selectedFitments.length !== 1 ? "s" : ""}? This action cannot be
            undone.
          </Text>
          <Group justify="flex-end">
            <Button variant="light" onClick={() => setDeleteModalOpen(false)}>
              Cancel
            </Button>
            <Button color="red" onClick={handleBulkDelete}>
              Delete Fitments
            </Button>
          </Group>
        </Stack>
      </Modal>
    </div>
  );
}

web/src/pages/ReviewPublish.tsx.content:
import { useEffect, useState } from "react";
import {
  Card,
  Title,
  Text,
  Button,
  Group,
  Stack,
  Table,
  Checkbox,
  Badge,
} from "@mantine/core";
import { reviewService, uploadsService } from "../api/services";

export default function ReviewPublish() {
  const [items, setItems] = useState<any[]>([]);
  const [selected, setSelected] = useState<Record<string, boolean>>({});
  const [uploadId, setUploadId] = useState<string | null>(null);

  const load = async () => {
    const res = await reviewService.list({ status: "pending" });
    setItems(res.data?.items || []);
  };
  useEffect(() => {
    load();
  }, []);

  const approve = async () => {
    const ids = Object.keys(selected).filter((k) => selected[k]);
    if (ids.length === 0) return;
    await reviewService.actions("approve", ids);
    setSelected({});
    await load();
  };

  const reject = async () => {
    const ids = Object.keys(selected).filter((k) => selected[k]);
    if (ids.length === 0) return;
    await reviewService.actions("reject", ids);
    setSelected({});
    await load();
  };

  const publish = async () => {
    if (!uploadId) {
      alert("Select a row to infer uploadId");
      return;
    }
    const res = await uploadsService.publish(uploadId);
    alert(`Published: ${res.data?.result?.publishedCount ?? 0}`);
  };

  return (
    <div style={{ padding: "24px 0" }}>
      <Stack gap="lg">
        <Card shadow="sm" padding="lg" radius="md" withBorder>
          <Title order={2}>Review & Publish</Title>
          <Text c="dimmed">
            Approve or reject pending items, then publish approved ones.
          </Text>
        </Card>

        <Card shadow="sm" padding="lg" radius="md" withBorder>
          <Group justify="space-between" mb="md">
            <Group>
              <Button onClick={approve}>Approve</Button>
              <Button color="red" onClick={reject}>
                Reject
              </Button>
            </Group>
            <Button color="green" onClick={publish}>
              Publish
            </Button>
          </Group>

          <Table striped highlightOnHover>
            <Table.Thead>
              <Table.Tr>
                <Table.Th>
                  <Checkbox
                    checked={
                      items.length > 0 && items.every((i) => selected[i.id])
                    }
                    indeterminate={
                      items.some((i) => selected[i.id]) &&
                      !items.every((i) => selected[i.id])
                    }
                    onChange={(e) => {
                      const c = e.currentTarget.checked;
                      const next: Record<string, boolean> = {};
                      items.forEach((i) => (next[i.id] = c));
                      setSelected(next);
                    }}
                  />
                </Table.Th>
                <Table.Th>ID</Table.Th>
                <Table.Th>Upload</Table.Th>
                <Table.Th>Row</Table.Th>
                <Table.Th>Confidence</Table.Th>
                <Table.Th>Status</Table.Th>
              </Table.Tr>
            </Table.Thead>
            <Table.Tbody>
              {items.map((i) => (
                <Table.Tr key={i.id} onClick={() => setUploadId(i.upload_id)}>
                  <Table.Td>
                    <Checkbox
                      checked={!!selected[i.id]}
                      onChange={(e) =>
                        setSelected((prev) => ({
                          ...prev,
                          [i.id]: e.currentTarget.checked,
                        }))
                      }
                    />
                  </Table.Td>
                  <Table.Td>{i.id}</Table.Td>
                  <Table.Td>{i.upload_id}</Table.Td>
                  <Table.Td>{i.row_index}</Table.Td>
                  <Table.Td>
                    <Badge>{Math.round((i.confidence || 0) * 100)}%</Badge>
                  </Table.Td>
                  <Table.Td>{i.status}</Table.Td>
                </Table.Tr>
              ))}
            </Table.Tbody>
          </Table>
        </Card>
      </Stack>
    </div>
  );
}

web/src/pages/UploadMap.tsx.content:
import { useState, useRef } from "react";
import {
  Card,
  Title,
  Text,
  Button,
  Group,
  Stack,
  Alert,
  Table,
  Badge,
  Center,
  rem,
} from "@mantine/core";
import {
  IconCloudUpload,
  IconWand,
  IconChecks,
  IconCheck,
} from "@tabler/icons-react";
import { uploadsService, presetsService } from "../api/services";

export default function UploadMap() {
  const [file, setFile] = useState<File | null>(null);
  const [uploadId, setUploadId] = useState<string | null>(null);
  const [suggestions, setSuggestions] = useState<any[]>([]);
  const fileRef = useRef<HTMLInputElement>(null);

  const handleSelect = (f: File) => {
    setFile(f);
  };

  const doUpload = async () => {
    if (!file) return;
    const res = await uploadsService.create(file, { tenantId: "default" });
    setUploadId(res.data?.id);
  };

  const doAiMap = async () => {
    if (!uploadId) return;
    const res = await uploadsService.aiMap(uploadId);
    setSuggestions(res.data?.suggestions?.columnMappings || []);
  };

  const doValidate = async () => {
    if (!uploadId) return;
    await uploadsService.vcdbValidate(uploadId);
    alert("Validation job created");
  };

  return (
    <div style={{ padding: "24px 0" }}>
      <Stack gap="lg">
        <Card shadow="sm" padding="lg" radius="md" withBorder>
          <Title order={2}>Upload & Map</Title>
          <Text c="dimmed">
            Upload your file, get AI mapping suggestions, and run VCDB
            validation.
          </Text>
        </Card>

        <Card shadow="sm" padding="lg" radius="md" withBorder>
          <Title order={3} mb="md">
            1. Upload
          </Title>
          <div
            onClick={() => fileRef.current?.click()}
            style={{
              border: "2px dashed var(--mantine-color-gray-4)",
              borderRadius: rem(8),
              padding: rem(40),
              textAlign: "center",
              cursor: "pointer",
            }}
          >
            <input
              ref={fileRef}
              type="file"
              accept=".csv,.tsv,.xlsx"
              style={{ display: "none" }}
              onChange={(e) =>
                e.target.files?.[0] && handleSelect(e.target.files[0])
              }
            />
            <Center>
              <Stack align="center" gap="sm">
                <IconCloudUpload size={48} />
                <Text fw={500}>
                  {file ? file.name : "Select CSV/TSV/XLSX file"}
                </Text>
                <Group>
                  <Button disabled={!file} onClick={doUpload}>
                    Upload
                  </Button>
                  {uploadId && (
                    <Badge color="green" leftSection={<IconCheck size={12} />}>
                      Uploaded
                    </Badge>
                  )}
                </Group>
              </Stack>
            </Center>
          </div>
        </Card>

        <Card shadow="sm" padding="lg" radius="md" withBorder>
          <Title order={3} mb="md">
            2. AI Mapping
          </Title>
          <Group>
            <Button
              leftSection={<IconWand size={16} />}
              disabled={!uploadId}
              onClick={doAiMap}
            >
              Get Suggestions
            </Button>
          </Group>
          {suggestions.length > 0 && (
            <Table mt="md">
              <Table.Thead>
                <Table.Tr>
                  <Table.Th>Source Column</Table.Th>
                  <Table.Th>VCDB Target</Table.Th>
                  <Table.Th>Confidence</Table.Th>
                </Table.Tr>
              </Table.Thead>
              <Table.Tbody>
                {suggestions.map((s, idx) => (
                  <Table.Tr key={idx}>
                    <Table.Td>{s.source}</Table.Td>
                    <Table.Td>{s.target}</Table.Td>
                    <Table.Td>
                      <Badge>{Math.round((s.confidence || 0) * 100)}%</Badge>
                    </Table.Td>
                  </Table.Tr>
                ))}
              </Table.Tbody>
            </Table>
          )}
        </Card>

        <Card shadow="sm" padding="lg" radius="md" withBorder>
          <Title order={3} mb="md">
            3. VCDB Validate
          </Title>
          <Group>
            <Button
              leftSection={<IconChecks size={16} />}
              disabled={!uploadId}
              onClick={doValidate}
            >
              Run Validation
            </Button>
          </Group>
        </Card>
      </Stack>
    </div>
  );
}

